#!/usr/bin/env bashSRC_DIR="jitsi-mac"TEMP_MOUNT=$(mktemp -d "${TMPDIR:-/tmp}/zombie.XXXXXXXXX")TEMP_DMG="Jitsi-tmp.dmg"DMG="jitsi.dmg"hdiutil create -srcfolder ${SRC_DIR} -volname "${APP_NAME}" -ov "${TEMP_DMG}" -format UDRWhdiutil attach "${TEMP_DMG}" -mountroot "${TEMP_MOUNT}/"SetFile -a C "${TEMP_MOUNT}/Jitsi"symlink "${TEMP_MOUNT}/Jitsi" /Applicationshdiutil detach "${TEMP_MOUNT}/Jitsi"hdiutil convert "${TEMP_DMG}" -format UDZO -o "${DMG}"